            rotate([0, 90, 0])
                cylinder(h=weave_thickness, r=weave_width/2, center=true);
    }
}

// Module for creating a horizontal ring at specified height
module horizontal_ring(height_pos) {
    // Calculate radius at this height
    radius_at_height = basket_bottom_radius + 
