desk_height = 75;  // 75cm (approx 30")
top_thickness = 2.5; // 2.5cm (approx 1")

// Leg dimensions
leg_width = 5;
leg_depth = 5;
leg_height = desk_height - top_thickness;
leg_inset = 5;

// Support bar dimensions
support_width = desk_width - (2 * leg_inset);
support_height = 5;
support_thickness = 2;
support_offset = 25; // Distance from top

// Optional features
add_cable_hole = true;
cable_hole_diameter = 6;
cable_hole_offset = 10;

/* Modules */
module desk_top() {
    // Main desktop surface
    cube([desk_width, desk_depth, top_thickness]);
}

module desk_leg() {
    // Single desk leg
    cube([leg_width, leg_depth, leg_height]);
}

module support_bar() {
    // Horizontal support bar between legs
    cube([support_width, support_thickness, support_height]);
}

module cable_hole() {
    // Cable management hole
    translate([desk_width/2, cable_hole_offset, -1])
        cylinder(h=top_thickness+2, d=cable_hole_diameter);
}

/* Assembly */
module desk_assembly() {
    // Desktop
    desk_top();

    // Legs at each corner with inset
    translate([leg_inset, leg_inset, -leg_height])
        desk_leg();
    translate([desk_width - leg_width - leg_inset, leg_inset, -leg_height])
        desk_leg();
    translate([leg_inset, desk_depth - leg_depth - leg_inset, -leg_height])
        desk_leg();
    translate([desk_width - leg_width - leg_inset, desk_depth - leg_depth - leg_inset, -leg_height])
        desk_leg();

    // Support bars
    translate([leg_inset, leg_inset + leg_depth/2 - support_thickness/2, -support_offset])
        support_bar();
    translate([leg_inset, desk_depth - leg_inset - leg_depth/2 - support_thickness/2, -support_offset])
        support_bar();

    // Optional cable hole
    if (add_cable_hole) {
        difference() {
            children();
            cable_hole();
        }
    }
}

