// Cup with Handle
// Author: OpenSCAD Expert
// Description: A simple cup with an ergonomic handle

/* Parameters */
// Cup dimensions
cup_diameter = 80;    // Outer diameter of the cup
cup_height = 95;      // Height of the cup
wall_thickness = 3;   // Thickness of the cup walls
bottom_thickness = 5; // Thickness of the cup bottom

// Handle dimensions
handle_width = 15;    // Width of the handle
handle_thickness = 8; // Thickness of the handle
handle_clearance = 20; // Space between cup and inner handle
handle_height = 60;   // Height of the handle
handle_position = 25; // Position from bottom of cup

// Aesthetic parameters
$fn = 100;            // Smoothness of curved surfaces

/* Modules */
// Main cup body
module cup_body() {
    difference() {
        // Outer cup shape
        cylinder(h=cup_height, d=cup_diameter);

        // Inner cup cavity
        translate([0, 0, bottom_thickness])
            cylinder(h=cup_height, d=cup_diameter - wall_thickness * 2);
    }
}

// Handle module using hull between cylinders for smooth curve
module handle() {
    hull() {
        // Top connection point
        translate([cup_diameter/2 - 1, 0, cup_height - handle_position])
            rotate([0, 90, 0])
                cylinder(h=handle_thickness, d=handle_width);

        // Bottom connection point
        translate([cup_diameter/2 - 1, 0, handle_position])
            rotate([0, 90, 0])
                cylinder(h=handle_thickness, d=handle_width);
    }

    // Outer curved part of handle
    hull() {
        // Top outer point
        translate([cup_diameter/2 + handle_clearance, 0, cup_height - handle_position])
            rotate([0, 90, 0])
                cylinder(h=handle_thickness, d=handle_width);

        // Bottom outer point
        translate([cup_diameter/2 + handle_clearance, 0, handle_position])
            rotate([0, 90, 0])
                cylinder(h=handle_thickness, d=handle_width);
    }

    // Top connecting piece
    hull() {
        translate([cup_diameter/2 - 1, 0, cup_height - handle_position])
            rotate([0, 90, 0])
                cylinder(h=handle_thickness, d=handle_width);

        translate([cup_diameter/2 + handle_clearance, 0, cup_height - handle_position])
            rotate([0, 90, 0])
                cylinder(h=handle_thickness, d=handle_width);
    }

    // Bottom connecting piece
    hull() {
        translate([cup_diameter/2 - 1, 0, handle_position])
            rotate([0, 90, 0])
                cylinder(h=handle_thickness, d=handle_width);

        translate([cup_diameter/2 + handle_clearance, 0, handle_position])
            rotate([0, 90, 0])
                cylinder(h=handle_thickness, d=handle_width);
    }
}

/* Main Assembly */
