// Simple Bed Model in OpenSCAD
// This parametric bed model includes frame, headboard, footboard, and slats

// Main bed dimensions
bed_width = 100;        // Width of the bed
bed_length = 200;       // Length of the bed
bed_height = 40;        // Height of bed from floor to top of mattress platform
frame_thickness = 5;    // Thickness of the bed frame
mattress_height = 20;   // Height of mattress (for visualization)

// Headboard and footboard dimensions
headboard_height = 60;  // Height of headboard above bed frame
footboard_height = 30;  // Height of footboard above bed frame
board_thickness = 3;    // Thickness of the headboard and footboard

// Slat dimensions
slat_width = 5;         // Width of each slat
slat_spacing = 5;       // Space between slats
slat_height = 2;        // Height/thickness of slats

// Leg dimensions
leg_height = bed_height - frame_thickness;
leg_width = 8;

// Colors
frame_color = [0.6, 0.4, 0.2];      // Brown for wooden frame
mattress_color = [0.9, 0.9, 1.0];   // Light blue for mattress
slat_color = [0.7, 0.5, 0.3];       // Lighter brown for slats

// Main assembly
bed();

// Bed module that combines all components
module bed() {
    // Bed frame
    color(frame_color)
        bed_frame();

    // Headboard
    color(frame_color)
        headboard();

    // Footboard
    color(frame_color)
        footboard();

    // Slats
    color(slat_color)
        slats();

    // Mattress (for visualization)
    color(mattress_color)
        translate([0, 0, frame_thickness + slat_height])
            cube([bed_width - 2*frame_thickness, 
                  bed_length - 2*frame_thickness, 
                  mattress_height]);

    // Legs
    color(frame_color)
        legs();
}

// Bed frame module
module bed_frame() {
    difference() {
        // Outer frame
        cube([bed_width, bed_length, frame_thickness]);

        // Inner cutout
        translate([frame_thickness, frame_thickness, -1])
            cube([bed_width - 2*frame_thickness, 
                  bed_length - 2*frame_thickness, 
                  frame_thickness + 2]);
    }
}

// Headboard module
module headboard() {
    translate([0, -board_thickness, 0])
        cube([bed_width, board_thickness, headboard_height + bed_height]);
}

// Footboard module
module footboard() {
    translate([0, bed_length, 0])
        cube([bed_width, board_thickness, footboard_height + bed_height]);
}

// Slats module
module slats() {
    num_slats = floor((bed_length - 2*frame_thickness) / (slat_width + slat_spacing));
    slat_length = bed_width - 2*frame_thickness;

    translate([frame_thickness, frame_thickness, frame_thickness]) {
        for (i = [0:num_slats-1]) {
            translate([0, i * (slat_width + slat_spacing), 0])
                cube([slat_length, slat_width, slat_height]);
        }
    }
}

// Legs module
module legs() {
    leg_positions = [
        [0, 0, 0],                              // Front left
        [bed_width - leg_width, 0, 0],          // Front right
        [0, bed_length - leg_width, 0],         // Back left
        [bed_width - leg_width, bed_length - leg_width, 0]  // Back right
    ];

    for (pos = leg_positions) {
        translate(pos)
            cube([leg_width, leg_width, leg_height]);
    }
}

// Parametric Sofa Model
// Dimensions are in mm

// Main sofa parameters
sofa_width = 1800;
sofa_depth = 800;
sofa_height = 800;
seat_height = 400;
arm_width = 150;
cushion_thickness = 100;
backrest_thickness = 150;
leg_height = 100;
leg_width = 60;

// Material parameters
$fn = 30; // Facet count for curved surfaces

// Main sofa base module
module sofa_base() {
    // Main seat base
    cube([sofa_width, sofa_depth, seat_height - leg_height]);

    // Backrest
    translate([0, sofa_depth - backrest_thickness, seat_height - leg_height])
        cube([sofa_width, backrest_thickness, sofa_height - seat_height + leg_height]);

    // Arms
    translate([0, 0, seat_height - leg_height])
        cube([arm_width, sofa_depth, sofa_height - seat_height + leg_height]);

    translate([sofa_width - arm_width, 0, seat_height - leg_height])
        cube([arm_width, sofa_depth, sofa_height - seat_height + leg_height]);
}

// Cushions module
module cushions() {
    cushion_width = (sofa_width - 2 * arm_width) / 2;
    cushion_depth = sofa_depth - backrest_thickness - 50;

    // Seat cushions
    for (i = [0:1]) {
        translate([arm_width + i * cushion_width, 50, seat_height - leg_height])
            rounded_box(cushion_width - 20, cushion_depth, cushion_thickness, 20);
    }

    // Back cushions
    back_cushion_height = sofa_height - seat_height - 100;
    for (i = [0:1]) {
        translate([arm_width + i * cushion_width, sofa_depth - backrest_thickness + 20, seat_height - leg_height + cushion_thickness])
            rounded_box(cushion_width - 20, backrest_thickness - 40, back_cushion_height, 20);
    }
}

// Legs module
module legs() {
    leg_positions = [
        [80, 80, 0],
        [sofa_width - 80, 80, 0],
        [80, sofa_depth - 80, 0],
        [sofa_width - 80, sofa_depth - 80, 0]
    ];

    for (pos = leg_positions) {
        translate(pos)
            tapered_leg(leg_height, leg_width);
    }
}

// Utility module for rounded box shape
module rounded_box(width, depth, height, radius) {
    hull() {
        for (x = [radius, width - radius]) {
            for (y = [radius, depth - radius]) {
                translate([x, y, radius])
                    sphere(radius);
                translate([x, y, height - radius])
                    sphere(radius);
            }
        }
    }
}

// Tapered leg module
module tapered_leg(height, width) {
    hull() {
        cube([width, width, 1]);
        translate([width/6, width/6, 0])
            cube([width*2/3, width*2/3, height]);
    }
}

// Decorative piping module
module piping() {
    // Seat front piping
    translate([arm_width, 50, seat_height - leg_height + cushion_thickness])
        rotate([0, 90, 0])
            cylinder(h=sofa_width - 2 * arm_width, r=5);

    // Backrest top piping
    translate([0, sofa_depth - backrest_thickness/2, sofa_height - 5])
        rotate([0, 90, 0])
            cylinder(h=sofa_width, r=5);
}

// Assemble the complete sofa
module sofa() {
    color("SaddleBrown") sofa_base();
    color("Tan") cushions();
    color("DarkBrown") legs();
    color("Chocolate") piping();
}

// Render the sofa
sofa();

