// Car rim with 5 spokes
// Parameters for customization
rim_outer_diameter = 200;
rim_inner_diameter = 180;
rim_width = 70;
hub_diameter = 80;
hub_width = 80;
spoke_width = 15;
spoke_thickness = 8;
center_hole_diameter = 60;
bolt_hole_diameter = 12;
bolt_circle_diameter = 110;
num_spokes = 5;
num_bolt_holes = 5;

// Main module for the entire rim
module car_rim() {
    difference() {
        union() {
            // Outer rim
            rim();

            // Hub
            hub();

            // Spokes
            for (i = [0:num_spokes-1]) {
                rotate([0, 0, i * (360 / num_spokes)])
                spoke();
            }
        }

        // Center hole
        translate([0, 0, -1])
        cylinder(h = hub_width + 2, d = center_hole_diameter, $fn = 100);

        // Bolt holes
        for (i = [0:num_bolt_holes-1]) {
            rotate([0, 0, i * (360 / num_bolt_holes)])
            translate([bolt_circle_diameter / 2, 0, -1])
            cylinder(h = hub_width + 2, d = bolt_hole_diameter, $fn = 50);
        }
    }
}

// Module for the outer rim
module rim() {
    difference() {
        cylinder(h = rim_width, d = rim_outer_diameter, center = true, $fn = 200);
        cylinder(h = rim_width + 1, d = rim_inner_diameter, center = true, $fn = 200);
    }
}

// Module for the central hub
module hub() {
    cylinder(h = hub_width, d = hub_diameter, center = true, $fn = 100);
}

// Module for a single spoke
module spoke() {
    // Calculate spoke length based on rim and hub diameters
    spoke_length = (rim_inner_diameter - hub_diameter) / 2;

    // Create a tapered spoke that connects hub to rim
    hull() {
        // Connection point at hub
        translate([hub_diameter / 2 - 2, 0, 0])
        scale([1, 0.8, 1])
        cylinder(h = spoke_thickness, d = spoke_width, center = true, $fn = 30);

        // Connection point at rim
        translate([rim_inner_diameter / 2 - 2, 0, 0])
        scale([1, 1.5, 1])
        cylinder(h = spoke_thickness, d = spoke_width, center = true, $fn = 30);
    }

    // Additional reinforcement at rim connection
    translate([rim_inner_diameter / 2 - 10, 0, 0])
    scale([1, 2, 1])
    cylinder(h = spoke_thickness, d = spoke_width, center = true, $fn = 30);
}

// Render the car rim
car_rim();

// Racing Car Rim - Parametric Design
// Author: OpenSCAD Expert
// Description: A detailed racing car rim with customizable parameters

// Main Parameters
rim_diameter = 450;        // Overall diameter of the rim in mm
rim_width = 200;           // Width of the rim in mm
center_bore = 60;          // Diameter of the center bore
spoke_count = 5;           // Number of spokes
bolt_count = 5;            // Number of bolt holes
bolt_circle_diameter = 120; // Diameter of bolt circle
bolt_hole_diameter = 14;   // Diameter of each bolt hole
lip_height = 15;           // Height of the rim lip
spoke_style = "Y";         // Can be "Y", "straight", or "curved"

// Derived parameters
rim_radius = rim_diameter / 2;
rim_inner_radius = rim_radius - 40;
spoke_width = rim_width * 0.5;
spoke_thickness = 15;

// Main module for the racing car rim
module racing_rim() {
    difference() {
        union() {
            // Outer rim
            outer_rim();

            // Spokes
            spokes();

            // Center hub
            center_hub();
        }

        // Center bore
        translate([0, 0, -1])
            cylinder(h = rim_width + 2, d = center_bore, $fn = 100);

        // Bolt holes
        bolt_holes();

        // Weight reduction cutouts on rim
        weight_reduction_cutouts();
    }
}

// Outer rim structure
module outer_rim() {
    difference() {
        // Main rim cylinder
        cylinder(h = rim_width, r = rim_radius, $fn = 200);

        // Hollow out the center
        translate([0, 0, -1])
            cylinder(h = rim_width + 2, r = rim_inner_radius, $fn = 200);

        // Bead seat profile (both sides)
        for (z = [0, rim_width]) {
            translate([0, 0, z])
                rotate_extrude($fn = 200)
                    translate([rim_radius - 20, 0, 0])
                        circle(r = 25, $fn = 50);
        }
    }

    // Add decorative lip on outer edge
    difference() {
        cylinder(h = rim_width, r = rim_radius + 5, $fn = 200);
        translate([0, 0, -1])
            cylinder(h = rim_width + 2, r = rim_radius, $fn = 200);
    }
}

// Spoke generation based on style parameter
module spokes() {
    for (i = [0:spoke_count-1]) {
        rotate([0, 0, i * (360 / spoke_count)])
            if (spoke_style == "Y") {
                y_spoke();
            } else if (spoke_style == "curved") {
                curved_spoke();
            } else {
                straight_spoke();
            }
    }
}

// Y-shaped spoke design
module y_spoke() {
    // Main arm
    hull() {
        translate([0, 0, rim_width/2])
            scale([1, 1, 0.5])
                sphere(r = center_bore/2 + 10);

        translate([rim_inner_radius * 0.7, 0, rim_width/2])
            rotate([90, 0, 0])
                cylinder(h = spoke_thickness, r = spoke_thickness, center = true, $fn = 50);
    }

    // Y branches
    for (angle = [-25, 25]) {
        hull() {
            translate([rim_inner_radius * 0.65, 0, rim_width/2])
                rotate([90, 0, 0])
                    cylinder(h = spoke_thickness, r = spoke_thickness, center = true, $fn = 50);

            rotate([0, 0, angle])
                translate([rim_inner_radius - 10, 0, rim_width/2])
                    rotate([90, 0, 0])
                        cylinder(h = spoke_thickness, r = spoke_thickness/1.5, center = true, $fn = 50);
        }
    }
}

// Curved spoke design
module curved_spoke() {
    width_factor = 1.5;

    for (offset = [-spoke_width/4, spoke_width/4]) {
        hull() {
            // Hub connection
            translate([center_bore/2 + 10, offset/width_factor, rim_width/2])
                sphere(r = 12, $fn = 30);

            // Middle control point with curve
            translate([rim_inner_radius * 0.5, offset, rim_width/2])
                sphere(r = 10, $fn = 30);

            // Rim connection
            translate([rim_inner_radius - 10, offset/width_factor, rim_width/2])
                sphere(r = 12, $fn = 30);
        }
    }
}

// Straight spoke design
module straight_spoke() {
    hull() {
        // Hub connection
        translate([0, 0, rim_width/2])
            cylinder(h = spoke_width, r = center_bore/2 + 10, center = true, $fn = 50);

        // Rim connection
        translate([rim_inner_radius - 10, 0, rim_width/2])
            rotate([90, 0, 0])
                cylinder(h = spoke_thickness, r = spoke_thickness, center = true, $fn = 50);
    }
}

// Center hub with reinforcement
module center_hub() {
    difference() {
        union() {
            // Main hub cylinder
            cylinder(h = rim_width, r = center_bore/2 + 20, $fn = 100);

            // Reinforcement ring
            translate([0, 0, rim_width/2])
                rotate_extrude($fn = 100)
                    translate([center_bore/2 + 20, 0, 0])
                        circle(r = 10, $fn = 30);
        }

        // Hollow out center
        translate([0, 0, -1])
            cylinder(h = rim_width + 2, r = center_bore/2, $fn = 100);
    }
}

// Bolt hole pattern
module bolt_holes() {
    for (i = [0:bolt_count-1]) {
        rotate([0, 0, i * (360 / bolt_count)])
            translate([bolt_circle_diameter/2, 0, -1])
                cylinder(h = rim_width + 2, d = bolt_hole_diameter, $fn = 40);
    }
}

// Weight reduction cutouts on the rim
module weight_reduction_cutouts() {
    cutout_count = spoke_count * 3;
    for (i = [0:cutout_count-1]) {
        rotate([0, 0, i * (360 / cutout_count) + 180/cutout_count])
            translate([rim_inner_radius + 15, 0, rim_width/2])
                rotate([0, 90, 0])
                    scale([1.5, 0.8, 1])
                        cylinder(h = 30, r = 12, center = true, $fn = 40);
    }
}

// Render the rim
racing_rim();

