                    cylinder(h=lip_height, r=rim_radius+5, center=true);
                translate([0, 0, -rim_width/2 + lip_height/2])
                    cylinder(h=lip_height, r=rim_radius+5, center=true);
            }

            // Hollow out the center
            cylinder(h=rim_width+1, r=rim_radius-20, center=true);
        }

        // Center bore
        cylinder(h=rim_width+2, r=center_bore/2, center=true);

        // Bolt holes
        for (i = [0 : bolt_count - 1]) {
            angle = i * 360 / bolt_count;
            translate([bolt_circle/2 * cos(angle), bolt_circle/2 * sin(angle), 0])
                cylinder(h=rim_width+2, r=bolt_diameter/2, center=true);
        }

        // Valve stem hole
        rotate([0, 0, 45])
            translate([rim_radius-10, 0, 0])
                rotate([90, 0, 0])
                    cylinder(h=50, r=4, center=true);
    }
}

// Spoke module
module spoke() {
    hull() {
        // Hub connection (narrower)
        translate([hub_radius+5, 0, 0])
            cube([10, spoke_width*0.8, spoke_thickness], center=true);

        // Rim connection (wider)
        translate([hub_radius+spoke_length-10, 0, 0])
            cube([10, spoke_width, spoke_thickness], center=true);
    }
}

// Spokes assembly
module spokes() {
    for (i = [0 : spoke_count - 1]) {
        angle = i * 360 / spoke_count;
        rotate([0, 0, angle])
            spoke();
    }
}

// Center hub
module hub() {
    difference() {
        union() {
            // Main hub cylinder
            cylinder(h=rim_width*0.8, r=hub_radius, center=true);

            // Hub cap mount
            translate([0, 0, 0])
                cylinder(h=rim_width*0.4, r=hub_radius+10, center=true);
        }

        // Center bore
        cylinder(h=rim_width+2, r=center_bore/2, center=true);

        // Bolt holes
        for (i = [0 : bolt_count - 1]) {
            angle = i * 360 / bolt_count;
            translate([bolt_circle/2 * cos(angle), bolt_circle/2 * sin(angle), 0])
                cylinder(h=rim_width+2, r=bolt_diameter/2, center=true);
        }

        // Decorative recesses
        for (i = [0 : bolt_count - 1]) {
            angle = i * 360 / bolt_count;
            rotate([0, 0, angle])
                translate([bolt_circle/2, 0, rim_width*0.2])
                    cylinder(h=rim_width*0.3, r=bolt_diameter, center=true);
        }
    }
}

// Final assembly
module car_rim() {
    rim();
    difference() {
        spokes();
        cylinder(h=rim_width+2, r=hub_radius, center=true);
    }
    hub();
}

