// Main rim parameters
rim_diameter = 400;       // Outer diameter of the rim in mm
rim_width = 180;          // Width of the rim in mm
hub_diameter = 100;       // Diameter of the center hub
hub_thickness = 20;       // Thickness of the hub
spoke_count = 5;          // Number of spokes
bolt_count = 5;           // Number of bolt holes
bolt_circle_diameter = 120; // Diameter of bolt circle
bolt_hole_diameter = 14;   // Diameter of bolt holes
center_bore_diameter = 60; // Center bore diameter
lip_height = 20;          // Height of outer lip
spoke_width = 30;         // Width of each spoke
spoke_thickness = 15;     // Thickness of spokes
valve_stem_position = 0;  // Angular position of valve stem hole in degrees

// Derived parameters
rim_radius = rim_diameter / 2;
rim_inner_radius = rim_radius - 25;
hub_radius = hub_diameter / 2;
bolt_circle_radius = bolt_circle_diameter / 2;

// Create the entire rim assembly
module car_rim() {
    difference() {
        union() {
            rim_outer_structure();
            spokes();
            hub();
        }
        // Remove material for center bore
        translate([0, 0, -1])
            cylinder(h = rim_width + 2, d = center_bore_diameter, $fn = 64);

        // Create bolt holes
        bolt_holes();

        // Valve stem hole
        valve_stem();
    }
}

// Outer rim structure
module rim_outer_structure() {
    difference() {
        cylinder(h = rim_width, r = rim_radius, $fn = 128);

        // Hollow out the inside
        translate([0, 0, -1])
            cylinder(h = rim_width + 2, r = rim_inner_radius, $fn = 128);

        // Create lip on both sides
        lip_inner_radius = rim_radius - 12;
        translate([0, 0, -1])
            cylinder(h = lip_height + 1, r = lip_inner_radius, $fn = 128);
        translate([0, 0, rim_width - lip_height])
            cylinder(h = lip_height + 1, r = lip_inner_radius, $fn = 128);
    }
}

// Hub structure
module hub() {
    translate([0, 0, (rim_width - hub_thickness) / 2])
        cylinder(h = hub_thickness, r = hub_radius, $fn = 64);
}

// Bolt hole pattern
module bolt_holes() {
    translate([0, 0, (rim_width - hub_thickness) / 2 - 1])
    for (i = [0 : bolt_count - 1]) {
        angle = i * 360 / bolt_count;
        translate([
            bolt_circle_radius * cos(angle),
            bolt_circle_radius * sin(angle),
            0
        ])
        cylinder(h = hub_thickness + 2, d = bolt_hole_diameter, $fn = 32);
    }
}

// Valve stem hole
module valve_stem() {
    valve_radius = rim_radius - 10;
    translate([
        valve_radius * cos(valve_stem_position),
        valve_radius * sin(valve_stem_position),
        rim_width / 2
    ])
    rotate([0, 90, 0])
    cylinder(h = 30, d = 8, $fn = 16, center = true);
}

// Spokes connecting hub to rim
module spokes() {
    spoke_inner_radius = hub_radius + 5;
    spoke_outer_radius = rim_inner_radius - 5;

    translate([0, 0, (rim_width - spoke_thickness) / 2])
    for (i = [0 : spoke_count - 1]) {
        angle = i * 360 / spoke_count;
        rotate([0, 0, angle])
        hull() {
            // Inner connection to hub
            translate([spoke_inner_radius, 0, 0])
                cylinder(h = spoke_thickness, d = spoke_width * 0.8, $fn = 32);

            // Outer connection to rim
            translate([spoke_outer_radius, 0, 0])
                cylinder(h = spoke_thickness, d = spoke_width, $fn = 32);

            // Additional shape to create curved spokes
            translate([spoke_inner_radius + (spoke_outer_radius - spoke_inner_radius) * 0.3, 
                      spoke_width * 0.25, 0])
                cylinder(h = spoke_thickness, d = spoke_width * 0.6, $fn = 32);

            translate([spoke_inner_radius + (spoke_outer_radius - spoke_inner_radius) * 0.3, 
                      -spoke_width * 0.25, 0])
                cylinder(h = spoke_thickness, d = spoke_width * 0.6, $fn = 32);
        }
    }
}

