// Bed Parameters
bed_type = "Queen"; // ["Twin", "Full", "Queen", "King"]
frame_height = 40;   // Height of bed frame
frame_thickness = 25; // Thickness of bed frame
leg_height = 150;     // Height of legs
mattress_height = 200; // Height of mattress

// Derived dimensions based on bed type
function get_bed_dimensions(type) =
    type == "Twin" ? [990, 1900] :
    type == "Full" ? [1370, 1900] :
    type == "Queen" ? [1520, 2030] :
    type == "King" ? [1930, 2030] : [1520, 2030]; // Default to Queen

bed_dimensions = get_bed_dimensions(bed_type);
bed_width = bed_dimensions[0];
bed_length = bed_dimensions[1];

// Main module to create the entire bed
module bed() {
    frame();
    translate([0, 0, frame_height + leg_height]) mattress();
    legs();
    headboard();
}

// Bed frame module
module frame() {
    difference() {
        // Outer frame
        translate([0, 0, leg_height]) 
            cube([bed_width + 2*frame_thickness, 
                  bed_length + 2*frame_thickness, 
                  frame_height]);

        // Inner cutout
        translate([frame_thickness, frame_thickness, leg_height - 1])
            cube([bed_width, bed_length, frame_height + 2]);
    }

    // Support slats
    slat_count = 12;
    slat_width = 50;
    slat_spacing = (bed_length - slat_width) / (slat_count - 1);

    for (i = [0:slat_count-1]) {
        translate([frame_thickness, frame_thickness + i * slat_spacing, leg_height + frame_height/2])
            cube([bed_width, slat_width, 10]);
    }
}

// Mattress module with slight deformation for realism
module mattress() {
    // Create a basic mattress shape
    difference() {
        minkowski() {
            cube([bed_width, bed_length, mattress_height - 20]);
            scale([1, 1, 0.2]) sphere(r=20);
        }

        // Cut off the bottom half of the sphere
        translate([-50, -50, -50])
            cube([bed_width + 100, bed_length + 100, 50]);
    }
}

// Bed legs module
module leg() {
    // Tapered leg design
    hull() {
        cube([frame_thickness, frame_thickness, 1]);
        translate([2, 2, 0])
            cube([frame_thickness-4, frame_thickness-4, leg_height]);
    }
}

module legs() {
    // Position legs at the corners
    translate([0, 0, 0]) leg();
    translate([bed_width + frame_thickness, 0, 0]) leg();
    translate([0, bed_length + frame_thickness, 0]) leg();
    translate([bed_width + frame_thickness, bed_length + frame_thickness, 0]) leg();

    // Add middle support legs for larger beds
    if (bed_type == "Queen" || bed_type == "King") {
        translate([0, (bed_length + frame_thickness)/2, 0]) leg();
        translate([bed_width + frame_thickness, (bed_length + frame_thickness)/2, 0]) leg();
    }
}

// Headboard module
module headboard() {
    headboard_height = 500;
    translate([-frame_thickness/2, -frame_thickness/2, 0]) {
        difference() {
            // Main headboard
            translate([0, 0, 0])
                cube([bed_width + 3*frame_thickness, frame_thickness*2, leg_height + frame_height + headboard_height]);

            // Decorative cutouts
            for (i = [0:4]) {
                translate([(bed_width + 3*frame_thickness)/5 * i + (bed_width + 3*frame_thickness)/10, -1, leg_height + frame_height + 100])
                    rotate([-90, 0, 0])
                        cylinder(h=frame_thickness*2+2, r=40, $fn=30);
            }
        }
    }
}

