        // Middle of handle
        translate([0, handle_length/2, 0])
            scale([0.8, 0.4, 0.8]) 
                sphere(d=handle_width);

        // Top of handle (narrower)
        translate([0, handle_length, 0])
            scale([0.5, 0.3, 0.6]) 
                sphere(d=handle_width);
    }
}

// Tine Module
module tine() {
    // Create a single curved tine
    hull() {
        // Base of tine
        cylinder(h=handle_thickness, d=tine_width, center=true);

        // Tip of tine (curved downward)
        translate([0, tine_length, -curve_factor])
            scale([0.7, 0.2, 0.7]) 
                sphere(d=tine_width);
    }
}

// Neck Module (connection between handle and tines)
module neck() {
    hull() {
        // Connection to handle
        translate([0, handle_length, 0])
            scale([0.5, 0.2, 0.6]) 
                sphere(d=handle_width);

        // Connection to tines
        width = (num_tines - 1) * tine_spacing + tine_width;
        translate([0, handle_length + 5, 0])
            scale([width/handle_width, 0.3, 0.7]) 
                sphere(d=handle_width);
    }
}

// Complete Fork Assembly
module fork() {
    // Add handle
    handle();

    // Add neck
    neck();

    // Add tines
    total_width = (num_tines - 1) * tine_spacing;
    start_x = -total_width / 2;

    translate([0, handle_length + 5, 0])
        for (i = [0:num_tines-1]) {
            translate([start_x + i * tine_spacing, 0, 0])
                tine();
        }
}

// Render the fork
$fn = 50; // Smoothness of curves
fork();

