// Coffee Cup Implementation
// Author: Assistant
// Description: Parametric coffee cup design with handle

// Global Parameters
$fn = 100; // Smoothness of curves

// Cup Dimensions
cup_height = 95;
cup_outer_diameter = 78;
cup_inner_diameter = 70;
wall_thickness = 4;
base_thickness = 5;
rim_radius = 2;

// Handle Parameters
handle_thickness = 8;
handle_width = 15;
handle_clearance = 28;
handle_angle = 260; // Degrees of handle arc

// Modules for cup components
module cup_body() {
    difference() {
        // Outer shell with slight taper
        cylinder(h=cup_height, 
                r1=cup_outer_diameter/2, 
                r2=cup_outer_diameter/2-2, 
                center=false);

        // Inner hollow with taper
        translate([0, 0, base_thickness])
            cylinder(h=cup_height, 
                    r1=cup_inner_diameter/2, 
                    r2=cup_inner_diameter/2-2, 
                    center=false);

        // Rim fillet
        translate([0, 0, cup_height-rim_radius])
            rotate_extrude()
                translate([cup_outer_diameter/2-rim_radius, 0, 0])
                    circle(r=rim_radius);
    }
}

module handle() {
    // Handle creation using toroidal segment
    rotate([0, 0, -45])
        translate([cup_outer_diameter/2 + handle_clearance/2, 0, cup_height*0.6])
            rotate([90, 0, 0])
                rotate_extrude(angle=handle_angle)
                    translate([handle_clearance/2, 0, 0])
                        scale([1, 0.8])
                            circle(d=handle_thickness);
}

module base_concave() {
    translate([0, 0, base_thickness/2])
        difference() {
            cylinder(h=base_thickness, 
                    r=cup_outer_diameter/2, 
                    center=true);
            translate([0, 0, -1])
                scale([1, 1, 0.2])
                    sphere(r=cup_outer_diameter/1.5);
        }
}

// Final Assembly
union() {
    cup_body();
    handle();
    base_concave();
}

// Global parameters
$fn = 100; // Smoothness of circular elements

// Cup dimensions
cup_height = 140;
base_diameter = 60;
top_diameter = 90;
wall_thickness = 1.5;
lid_height = 5;
lid_overlap = 5;
drinking_hole_diameter = 8;

// Derived measurements
base_radius = base_diameter / 2;
top_radius = top_diameter / 2;

// Main cup body module
module cup_body() {
    difference() {
        // Outer shell
        cylinder(h = cup_height, 
                r1 = base_radius, 
                r2 = top_radius);

        // Inner hollow
        translate([0, 0, wall_thickness])
            cylinder(h = cup_height + 1, 
                    r1 = base_radius - wall_thickness, 
                    r2 = top_radius - wall_thickness);
    }

    // Base reinforcement
    cylinder(h = wall_thickness * 2, 
            r = base_radius);
}

// Lid module
module lid() {
    difference() {
        union() {
            // Main lid body
            cylinder(h = lid_height, 
                    r = top_radius + lid_overlap);

            // Lip for securing
            translate([0, 0, lid_height - 1])
                difference() {
                    cylinder(h = 2, 
                           r = top_radius);
                    translate([0, 0, -0.5])
                        cylinder(h = 3, 
                               r = top_radius - wall_thickness);
                }
        }

        // Drinking hole
        translate([top_radius/2, 0, 0])
            cylinder(h = lid_height * 3, 
                    r = drinking_hole_diameter/2, 
                    center = true);
    }
}

// Optional sleeve module
module sleeve() {
    height = cup_height * 0.6;
    thickness = 2;

    translate([0, 0, cup_height * 0.2])
        difference() {
            cylinder(h = height, 
                    r1 = base_radius + thickness * 2, 
                    r2 = top_radius + thickness);
            cylinder(h = height + 1, 
                    r1 = base_radius + thickness, 
                    r2 = top_radius);
        }
}

// Assembly
cup_body();
translate([0, 0, cup_height]) lid();
sleeve();

// Bench Parameters
bench_length = 100;    // Length of the bench in mm
bench_width = 30;      // Width of the bench in mm
bench_height = 45;     // Total height of the bench in mm
seat_thickness = 5;    // Thickness of the seat in mm
leg_width = 5;         // Width of each leg in mm
leg_height = bench_height - seat_thickness; // Height of each leg
support_thickness = 3; // Thickness of the support beams

// Module for the Seat
module seat() {
    cube([bench_length, bench_width, seat_thickness], center = false);
}

// Module for a Leg
module leg() {
    cube([leg_width, leg_width, leg_height], center = false);
}

// Module for Support Beams between Legs
module support() {
    // Horizontal support between legs
    cube([bench_length - 2 * leg_width, support_thickness, support_thickness], center = false);
}

// Assemble the Bench
module bench() {
    // Create the seat
    translate([0, 0, leg_height])
        seat();

    // Front Left Leg
    translate([0, 0, 0])
        leg();

    // Front Right Leg
    translate([bench_length - leg_width, 0, 0])
        leg();

    // Back Left Leg
    translate([0, bench_width - leg_width, 0])
        leg();

    // Back Right Leg
    translate([bench_length - leg_width, bench_width - leg_width, 0])
        leg();

    // Front Support
    translate([leg_width, (bench_width - support_thickness) / 2, leg_height - support_thickness])
        support();

    // Back Support
    translate([leg_width, (bench_width - support_thickness) / 2, leg_height - support_thickness + seat_thickness])
        support();
}

// Render the Bench
bench();

// Lamp Design in OpenSCAD

// Base Parameters
base_radius = 40;
base_height = 10;

// Stem Parameters
stem_radius = 5;
stem_height = 60;

// Shade Parameters
shade_radius_bottom = 20;
shade_radius_top = 10;
shade_height = 30;

// Bulb Holder Parameters
bulb_radius = 8;

// Module for Base
module base() {
    cylinder(
        h = base_height,
        r = base_radius,
        center = false
    );
}

// Module for Stem
module stem() {
    cylinder(
        h = stem_height,
        r = stem_radius,
        center = false
    );
}

// Module for Shade
module shade() {
    translate([0, 0, stem_height])
        cylinder(
            h = shade_height,
            r1 = shade_radius_bottom,
            r2 = shade_radius_top,
            center = false
        );
}

// Module for Bulb Holder
module bulb_holder() {
    translate([0, 0, stem_height + shade_height/2])
        sphere(r = bulb_radius);
}

// Assemble the Lamp
union() {
    // Base
    base();

    // Stem
    stem();

    // Shade
    shade();

    // Bulb Holder
    bulb_holder();
}

// Fence Dimensions
posts_count = 8;            // Total number of fence posts
post_width  = 10;           // Width of each post (x-direction)
post_depth  = 10;           // Depth of each post (y-direction)
post_height = 50;           // Height of each post (z-direction)
post_spacing = 40;          // Horizontal spacing between posts

rail_thickness = 3;         // Thickness of each rail (z-direction)
rail_depth     = 6;         // Depth (y-direction) of each rail
rail1_z        = 15;        // Height of the lower rail (z-position)
rail2_z        = 35;        // Height of the upper rail (z-position)

// Calculate overall fence length along the x-axis
fence_length = (posts_count - 1) * post_spacing + post_width;

// Module for a single fence post
module fence_post() {
  // Create a post as a cube with corner at [0,0,0]
  cube([post_width, post_depth, post_height]);
}

// Module for a continuous fence rail
// The rail extends along the entire length of the fence.
module fence_rail(z_position) {
  // Calculate the offset to center the rail along the depth of the post
  rail_y_offset = (post_depth - rail_depth) / 2;
  // Translate to the proper position and create the rail as a cube
  translate([0, rail_y_offset, z_position])
    cube([fence_length, rail_depth, rail_thickness]);
}

// Main assembly of the fence
union() {
  // Create fence posts
  for(i = [0 : posts_count - 1]) {
    // Translate each post along the x-axis at intervals defined by post_spacing
    translate([i * post_spacing, 0, 0])
      fence_post();
  }

  // Create horizontal rails
  fence_rail(rail1_z);  // Lower rail
  fence_rail(rail2_z);  // Upper rail
}

