// Medieval Sword Model
// This model creates a basic sword with blade, crossguard, grip and pommel

// Sword dimensions
blade_length = 70;
blade_width = 4;
blade_thickness = 0.8;
blade_taper = 0.7;  // How much the blade tapers toward the tip

crossguard_width = 15;
crossguard_height = 2;
crossguard_thickness = 1.5;

grip_length = 12;
grip_diameter = 3;
grip_taper = 0.9;   // Slight taper for the grip

pommel_diameter = 4;
pommel_height = 2;

fuller_depth = 0.2;
fuller_width = 1.5;
fuller_length = blade_length * 0.7;

// Module for the blade with fuller (blood groove)
module blade() {
    difference() {
        // Main blade shape with taper
        hull() {
            translate([0, 0, 0])
                cube([blade_width, blade_thickness, 0.01], center=true);

            translate([0, 0, blade_length])
                cube([blade_width * blade_taper, blade_thickness * blade_taper, 0.01], center=true);
        }

        // Fuller (blood groove)
        translate([0, 0, blade_length * 0.2])
            scale([fuller_width, fuller_depth, fuller_length])
                rotate([90, 0, 0])
                    cylinder(h=3, r=1, center=true, $fn=30);
    }

    // Blade edge bevels
    difference() {
        hull() {
            translate([0, 0, 0])
                cube([blade_width, blade_thickness, 0.01], center=true);

            translate([0, 0, blade_length])
                cube([blade_width * blade_taper, blade_thickness * blade_taper, 0.01], center=true);
        }

        // Side bevels
        hull() {
            translate([0, 0, 0])
                cube([blade_width - 0.5, blade_thickness - 0.4, 0.01], center=true);

            translate([0, 0, blade_length])
                cube([(blade_width - 0.5) * blade_taper, 
                     (blade_thickness - 0.4) * blade_taper, 0.01], center=true);
        }
    }
}

// Module for the crossguard
module crossguard() {
    difference() {
        // Main crossguard body
        cube([crossguard_width, crossguard_thickness, crossguard_height], center=true);

        // Slot for the blade
        cube([blade_width + 0.2, blade_thickness + 0.2, crossguard_height + 1], center=true);
    }
}

// Module for the grip with texture
module grip() {
    difference() {
        // Basic grip cylinder with taper
        hull() {
            cylinder(h=0.01, d=grip_diameter, center=true, $fn=30);
            translate([0, 0, grip_length])
                cylinder(h=0.01, d=grip_diameter * grip_taper, center=true, $fn=30);
        }

        // Texture pattern (diagonal grooves)
        for (i = [-20:4:20]) {
            rotate([0, 0, i])
                translate([0, 0, grip_length/2])
                    rotate([0, 2, 0])
                        cube([grip_diameter * 2, 0.2, grip_length * 1.2], center=true);
        }
    }
}

// Module for the pommel
module pommel() {
    union() {
        // Main pommel body
        sphere(d=pommel_diameter, $fn=30);

        // Pommel cap
        translate([0, 0, pommel_diameter/2 - 0.2])
            cylinder(h=pommel_height/2, d1=pommel_diameter, d2=pommel_diameter * 0.8, $fn=30);
    }
}

// Assemble the sword
module sword() {
    // Blade
    color("silver")
        translate([0, 0, crossguard_height/2 + grip_length + pommel_height])
            blade();

    // Crossguard
    color("gold")
        translate([0, 0, grip_length + pommel_height])
            crossguard();

    // Grip
    color("brown")
        translate([0, 0, pommel_height])
            grip();

    // Pommel
    color("gold")
        pommel();
}

// Render the sword
sword();

// Mailbox Model in OpenSCAD
// Units are in mm

// Main dimensions
mailbox_length = 180;
mailbox_width = 80;
mailbox_base_height = 60;
mailbox_top_radius = 40;
wall_thickness = 3;

// Door dimensions
door_thickness = 2;
door_clearance = 1;

// Flag dimensions
flag_width = 30;
flag_height = 50;
flag_thickness = 2;
flag_pole_radius = 3;
flag_offset = 15;

// Post dimensions
post_width = 40;
post_height = 480;

// Main mailbox body module
module mailbox_body() {
    difference() {
        union() {
            // Base rectangular part
            cube([mailbox_length, mailbox_width, mailbox_base_height]);

            // Semi-cylindrical top
            translate([0, mailbox_width/2, mailbox_base_height])
                rotate([0, 90, 0])
                    cylinder(h=mailbox_length, r=mailbox_width/2);
        }

        // Hollow out the inside, leaving walls with thickness
        translate([wall_thickness, wall_thickness, wall_thickness])
            union() {
                cube([mailbox_length - 2*wall_thickness, 
                      mailbox_width - 2*wall_thickness, 
                      mailbox_base_height - wall_thickness]);

                translate([0, (mailbox_width - 2*wall_thickness)/2, mailbox_base_height - wall_thickness])
                    rotate([0, 90, 0])
                        cylinder(h=mailbox_length - 2*wall_thickness, 
                                r=(mailbox_width - 2*wall_thickness)/2);
            }

        // Door opening
        translate([-1, wall_thickness + door_clearance, wall_thickness + door_clearance])
            cube([wall_thickness + 2, 
                  mailbox_width - 2*wall_thickness - 2*door_clearance, 
                  mailbox_base_height - 2*wall_thickness - door_clearance]);
    }
}

// Door module
module door() {
    door_width = mailbox_width - 2*wall_thickness - 2*door_clearance;
    door_height = mailbox_base_height - 2*wall_thickness - door_clearance;

    difference() {
        union() {
            // Door panel
